import './App.css';
import Content from './component/posts';


function App() {
  const [posts, setPosts] = useState(null)
  const fetchData = async ()=> {
    const res = await fetch('https://cataas.com/cat')
    const data = await res.json()
    return data
  }
  useEffect(()=>{
    (async()=>{
      const data = await fetchData()
      setPosts(data)
    })()
  }, [])
  return
