import { useEffect, useState } from 'react';
import './App.css';
import Content from './component/posts';


function App() {
  const [posts, setPosts] = useState([])
  const fetchData = async ()=> {
    const res = await fetch('https://cataas.com/')
    const data = await res.json()
    return data
  }
  useEffect(()=>{
    (async()=>{
      const data = await fetchData()
      setPosts(data)
    })()
  }, [])

  return (
  posts.map((item, idx)=>(
      <div key={idx}>
        <Content title={item.title}/>
      </div>
    ))
  )
}

export default App;
